options.customCss:
:root {
	--font-family: 'Inter', 'Segoe UI', -apple-system, BlinkMacSystemFont, sans-serif;
	--font-weight-normal: 400;
	--font-weight-bold: 600;
	--font-size-body: 14px;
	--font-size-label: 15px;
	--font-size-test-notice: 13px;
	--font-size-input: 16px;
	--font-size-header: 24px;
	--font-size-paragraph: 15px;
	--font-size-link: 14px;
	--font-size-error: 13px;
	--font-size-html-h1: 32px;
	--font-size-html-h2: 24px;
	--font-size-html-h3: 20px;
	--font-size-html-h4: 16px;
	--font-size-html-h5: 14px;
	--font-size-html-h6: 12px;
	--font-size-subheader: 16px;
	/* Colors */
	--color-background: #f8fafc;
	--color-test-notice-text: #f59e0b;
	--color-test-notice-bg: #fffbeb;
	--color-test-notice-border: #fed7aa;
	--color-card-bg: #ffffff;
	--color-card-border: #e2e8f0;
	--color-card-shadow: rgba(59, 130, 246, 0.08);
	--color-link: #3b82f6;
	--color-header: #1e293b;
	--color-label: #374151;
	--color-input-border: #d1d5db;
	--color-input-text: #1f2937;
	--color-focus-border: #3b82f6;
	--color-submit-btn-bg: #3b82f6;
	--color-submit-btn-text: #ffffff;
	--color-error: #ef4444;
	--color-required: #8b5cf6;
	--color-clear-button-bg: #6b7280;
	--color-html-text: #374151;
	--color-html-link: #3b82f6;
	--color-header-subtext: #64748b;
	/* Border Radii */
	--border-radius-card: 16px;
	--border-radius-input: 12px;
	--border-radius-clear-btn: 50%;
	--card-border-radius: 16px;
	/* Spacing */
	--padding-container-top: 32px;
	--padding-card: 32px;
	--padding-test-notice-vertical: 16px;
	--padding-test-notice-horizontal: 20px;
	--margin-bottom-card: 24px;
	--padding-form-input: 16px;
	--card-padding: 32px;
	--card-margin-bottom: 24px;
	/* Dimensions */
	--container-width: 480px;
	--submit-btn-height: 56px;
	--checkbox-size: 20px;
	/* Others */
	--box-shadow-card: 0px 8px 32px 0px var(--color-card-shadow);
	--opacity-placeholder: 0.6;
}
