// n8n Function node
// This function expects input items from Airtable (or a single item
// whose json is an array or has a .records array).
// It returns one item with json.inline_keyboard

// collect input payload(s)
const rawItems = items; // 'items' is provided by n8n Function node

// helper to extract records regardless of shape
let records = [];

// Case A: single item whose .json is an array (you pasted raw array)
if (rawItems.length === 1 && Array.isArray(rawItems[0].json)) {
  records = rawItems[0].json;
}
// Case B: single item with `.json.records`
else if (rawItems.length === 1 && Array.isArray(rawItems[0].json.records)) {
  records = rawItems[0].json.records;
}
// Case C: each item is one record (common n8n output from Airtable node)
else {
  for (let it of rawItems) {
    if (it.json && (it.json.id || it.json.name || it.json.fields)) {
      // normalize: if fields exist, merge fields to top-level for convenience
      const r = Object.assign({}, it.json);
      if (r.fields && typeof r.fields === 'object') {
        // copy fields (but keep id if present)
        r.fields = r.fields; // keep for later
      }
      records.push(r);
    } else {
      // fallback - if json is simple, add it
      records.push(it.json);
    }
  }
}

// build inline keyboard (2 buttons per row)
const keyboard = [];
for (let i = 0; i < records.length; i += 2) {
  const row = [];
  for (let j = i; j < i + 2 && j < records.length; j++) {
    const r = records[j];

    // label: prefer r.name, or r.fields.name, or r.fields["Name"]
    const label =
      (r.name && String(r.name)) ||
      (r.fields && (r.fields.name || r.fields.Name)) ||
      `${j + 1}`;

    // id: prefer numeric/simple id if available
    const id =
      (r.id && String(r.id)) ||
      (r.recordId && String(r.recordId)) ||
      (r.fields && (r.fields.id || r.fields.recordId)) ||
      String(j + 1);

    // build callback_data - keep it short (must be <=64 bytes)
    let callback = `${id}`;
    if (callback.length > 60) {
      // safety: truncate id part
      const shortId = id.slice(0, 40);
      callback = `${shortId}`;
    }

    row.push({
      text: label,
      callback_data: callback
    });
  }
  keyboard.push(row);
}

// return inline_keyboard in json (single item)
return [
  {
    json: {
      inline_keyboard: keyboard
    }
  }
];

