// Convert Excel rows â†’ PROPER JSON array ready for Twilio
const MAX_ITEMS = 10;
  const TITLE_MAX = 24;
const DESC_MAX = 72;

const rows = $input.all().map(i => i.json);

function trunc(s,n){ 
  if (!s) return ""; 
  s = String(s); 
  return s.length<=n ? s : s.slice(0,n-1)+"â€¦"; 
}

// Order by in-stock first
const inStock = rows.filter(r => (r.availability || '').toLowerCase() === 'in stock');
const others   = rows.filter(r => !inStock.includes(r));
const ordered  = inStock.concat(others).slice(0, MAX_ITEMS);

// Build Twilio List Picker items
const listItems = ordered.map(p => {
  const id = String(p.id);
  const title = trunc(p.title, TITLE_MAX);
  const description = trunc(p.description ?? `â‚¹${p.price} â€¢ ${p.availability}`, DESC_MAX);
  return {
    item: title,
    description: description,
    id: id
  };
});

// Return BOTH versions:
// 1. Normal JSON array
// 2. Stringified JSON array (for display)
return [{
  json: {
    items: listItems,
    items_stringified: JSON.stringify(listItems, null, 2)
  }
}];

// Function node: Quantity List Builder
// Input: previous node is your Twilio webhook (so product selection data is in $json)
// Output: { body, button, items_stringified }

// --- Config
const MAX_Q = 10;
const BUTTON_TEXT = 'Select Qty';     // <=20 chars
const BODY_PREFIX = 'You selected';   // used to build the message

// --- Helpers
function trunc(s, n=60){ if (s===undefined||s===null) return ''; s=String(s); return s.length<=n? s : s.slice(0,n-1)+'â€¦'; }

// --- Extract product identifier from common Twilio webhook fields
// Try a few common locations: (adjust if your webhook stores elsewhere)

// Common Twilio places seen in screenshots: payload.Body, payload.ListTitle, payload.body.ListTitle, payload.body.Body
let productIdentifier = $input.first().json.body.Body;
let productTitle = $input.first().json.body.ListTitle;


// --- Build items array (1..MAX_Q). Use small 'item' titles and descriptions
const items = [];
for (let q = 1; q <= MAX_Q; q++) {
  const idObj = `${q}_${productIdentifier}`;
  items.push({
    item: String(q),                    // label shown to the user
    description: q === 1 ? '1 unit' : `${q} units`,
    // id is a safe encoded JSON string (decode on webhook parse)
    id: idObj
  });
}

// Build output fields
const body = `${BODY_PREFIX} '${trunc(productTitle, 36)}'. How many units do you want?`;
const button = BUTTON_TEXT;
const items_stringified = JSON.stringify(items); // stringified JSON array as requested

return [{
  json: {
    body,
    button,
    items_stringified
  }
}];

