// Process OpenAI RSS feed items
const items = $input.all();
const processedItems = [];

for (const item of items) {
  processedItems.push({
    source: 'OpenAI',
    title: item.json.title,
    link: item.json.link,
    pubDate: item.json.pubDate || item.json.isoDate,
    description: item.json.contentSnippet || item.json.content?.substring(0, 200) || '',
    uniqueId: `openai-${item.json.link}`
  });
}

return processedItems.map(item => ({ json: item }));

