// Filter out already seen updates using Static Data
const items = $input.all();
const staticData = $getWorkflowStaticData('global');

console.log(`static data is ${staticData.seenItems}`)

// Initialize seen items array if it doesn't exist
if (!staticData.seenItems) {
  staticData.seenItems = [];
}

const newItems = [];

for (const item of items) {
  const uniqueId = item.json.uniqueId;

  // Check if we've seen this item before
  if (!staticData.seenItems.includes(uniqueId)) {
    newItems.push(item.json);
    staticData.seenItems.push(uniqueId);
  }
}

// Keep only the last n items to prevent unlimited growth
const n_items = 300000
if (staticData.seenItems.length > n_items) {
  staticData.seenItems = staticData.seenItems.slice(-n_items);
}

console.log(`Total items checked: ${items.length}`);
console.log(`New items found: ${newItems.length}`);
console.log(`Total tracked items: ${staticData.seenItems.length}`);

// Return new items or empty array if none
if (newItems.length === 0) {
  return [];
}

return newItems.map(item => ({ json: item }));

// Format Slack message with all new updates
const items = $input.all();

if (items.length === 0) {
  return [];
}

// Group items by source
const groupedBySource = {};
for (const item of items) {
  const source = item.json.source;
  if (!groupedBySource[source]) {
    groupedBySource[source] = [];
  }
  groupedBySource[source].push(item.json);
}

// Create Slack blocks
const blocks = [
  {
    type: "header",
    text: {
      type: "plain_text",
      text: "ðŸš¨ New Platform Updates Detected!",
      emoji: true
    }
  },
  {
    type: "context",
    elements: [
      {
        type: "mrkdwn",
        text: `Found ${items.length} new update${items.length > 1 ? 's' : ''} â€¢ ${new Date().toLocaleString()}`
      }
    ]
  },
  {
    type: "divider"
  }
];

// Add sections for each source
for (const [source, updates] of Object.entries(groupedBySource)) {
  // Source header
  blocks.push({
    type: "section",
    text: {
      type: "mrkdwn",
      text: `*${source}* (${updates.length} update${updates.length > 1 ? 's' : ''})`
    }
  });

  // Add each update
  for (const update of updates) {
    const dateStr = update.pubDate ? new Date(update.pubDate).toLocaleDateString('en-US', { 
      month: 'short', 
      day: 'numeric', 
      year: 'numeric' 
    }) : 'Recent';

    let text = `â€¢ *<${update.link}|${update.title}>*\n   _${dateStr}_`;

    if (update.description) {
      const shortDesc = update.description.length > 150 
        ? update.description.substring(0, 150) + '...' 
        : update.description;
      text += `\n   ${shortDesc.replace(/\n/g, ' ')}`;
    }

    blocks.push({
      type: "section",
      text: {
        type: "mrkdwn",
        text: text
      }
    });
  }

  blocks.push({
    type: "divider"
  });
}

// Create the final message
const slackMessage = {
  text: `ðŸš¨ ${items.length} New Platform Update${items.length > 1 ? 's' : ''} Detected!`,
  blocks: blocks
};

return [{ json: slackMessage }];

