// Process the editor's output and prepare for Google Sheets
const editorOutput = $input.first().json;

// Try to parse the JSON response from the editor
let newsData;
try {
  // If the editor returned a JSON string, parse it
  if (typeof editorOutput.text === 'string') {
    newsData = JSON.parse(editorOutput.text);
  } else {
    newsData = editorOutput;
  }
} catch (error) {
  // Fallback if JSON parsing fails
  newsData = {
    topic: $('Research Settings').item.json.topic,
    date_researched: new Date().toISOString().split('T')[0],
    executive_summary: 'Research completed - see raw data',
    key_developments: ['Processing error - check raw data'],
    impact_assessment: 'Unknown',
    notable_entities: 'N/A',
    top_sources: ['N/A'],
    sentiment: 'Neutral',
    category: 'Other',
    relevance_score: 5
  };
}

// Format for Google Sheets
const sheetsData = {
  Topic: newsData.topic || $('Research Settings').item.json.topic,
  Date_Researched: newsData.date_researched || new Date().toISOString().split('T')[0],
  Executive_Summary: newsData.executive_summary || 'N/A',
  Key_Developments: Array.isArray(newsData.key_developments) ? 
    newsData.key_developments.join('; ') : 
    String(newsData.key_developments || 'N/A'),
  Impact_Assessment: newsData.impact_assessment || 'N/A',
  Notable_Entities: newsData.notable_entities || 'N/A',
  Top_Sources: Array.isArray(newsData.top_sources) ? 
    newsData.top_sources.join('; ') : 
    String(newsData.top_sources || 'N/A'),
  Sentiment: newsData.sentiment || 'Neutral',
  Category: newsData.category || 'Other',
  Relevance_Score: newsData.relevance_score || 5,
  Status: 'Completed',
  Raw_Research_Data: JSON.stringify(editorOutput, null, 2)
};

return [sheetsData];
