// Filter articles from last 48 hours and relevant to AI + Automation
const now = new Date();
const twoDaysAgo = new Date(now.getTime() - (48 * 60 * 60 * 1000));

// Create a Set to track unique articles (by title or link)
const uniqueArticles = new Map();

items.forEach(item => {
  const articleDate = new Date(item.json.pubDate || item.json.date);
  const isRecent = articleDate >= twoDaysAgo;

  const title = (item.json.title || '').toLowerCase();
  const description = (item.json.description || '').toLowerCase();

  // Enhanced keywords for automation focus
  const automationKeywords = [
    'automation', 'automate', 'workflow', 'ai agent', 'agentic', 'no-code', 'low-code',
    'zapier', 'n8n', 'make.com', 'power automate', 'robotic process automation', 'rpa',
    'chatbot', 'ai assistant', 'copilot', 'ai tools', 'productivity', 'integration'
  ];

  const aiKeywords = [
    'ai', 'artificial intelligence', 'machine learning', 'ml', 'neural', 'gpt', 'llm',
    'deep learning', 'generative ai', 'openai', 'anthropic', 'claude', 'langchain'
  ];

  const allKeywords = [...automationKeywords, ...aiKeywords];

  const isRelevant = allKeywords.some(keyword => 
    title.includes(keyword) || description.includes(keyword)
  );

  // Only add if recent, relevant, and not already added
  if (isRecent && isRelevant) {
    const uniqueKey = item.json.link || item.json.title;
    if (!uniqueArticles.has(uniqueKey)) {
      uniqueArticles.set(uniqueKey, {
        title: item.json.title,
        description: item.json.description || '',
        link: item.json.link,
        pubDate: item.json.pubDate || item.json.date,
        source: item.json.source || 'RSS Feed',
        dateObj: articleDate
      });
    }
  }
});

// Convert Map to array, sort by date (newest first) and limit to top 10
const sortedItems = Array.from(uniqueArticles.values())
  .sort((a, b) => b.dateObj - a.dateObj)
  .slice(0, 10);

// Return formatted items without dateObj
return sortedItems.map(item => ({
  json: {
    title: item.title,
    description: item.description,
    link: item.link,
    pubDate: item.pubDate,
    source: item.source
  }
}));

// Combine all articles into a single string for summarization
const articles = items.map(item => {
  return `Title: ${item.json.title}\nDescription: ${item.json.description}\nSource: ${item.json.source}\nDate: ${item.json.pubDate}\nLink: ${item.json.link}\n---`;
}).join('\n\n');

return [{
  json: {
    articles: articles,
    articleCount: items.length,
    processedAt: new Date().toISOString(),
    individualArticles: items.map(item => item.json)
  }
}];

// Format the AI summary and articles for Slack
const summary = $input.first().json.message.content;
const articles = $input.first().json.individualArticles || [];
const articleCount = articles.length;

// Create the main Slack message
let slackMessage = `ü§ñ *Daily AI Automation News Digest*\n\n`;
slackMessage += `üì∞ *Today's Summary* (${articleCount} articles):\n`;
slackMessage += `${summary}\n\n`;

if (articles.length > 0) {
  slackMessage += `üìã *Top Articles:*\n`;

  // Add top 5 articles with links
  articles.slice(0, 5).forEach((article, index) => {
    const title = article.title.length > 80 ? article.title.substring(0, 80) + '...' : article.title;
    slackMessage += `${index + 1}. <${article.link}|${title}>\n`;
    slackMessage += `   _${article.source}_\n\n`;
  });
}

slackMessage += `‚è∞ Generated at ${new Date().toLocaleString('en-US', { timeZone: 'America/New_York', timeStyle: 'short', dateStyle: 'short' })} ET`;

return [{
  json: {
    text: slackMessage,
    articleCount: articleCount,
    processedAt: new Date().toISOString()
  }
}];
