// Build final summary - NO external dependencies

if ($input.all().length === 1 && $input.all()[0].json.message) {
  return $input.all(); // Pass through "no new items" message
}

// Group by source
const grouped = {};
const allItems = [];

for (const item of $input.all()) {
  const data = item.json;
  allItems.push(data);

  if (!grouped[data.source]) {
    grouped[data.source] = [];
  }
  grouped[data.source].push(data);
}

// Sort by date
for (const source in grouped) {
  grouped[source].sort((a, b) => {
    const dateA = new Date(a.publishedAt || 0);
    const dateB = new Date(b.publishedAt || 0);
    return dateB - dateA;
  });
}

// Build summary
const summary = {
  totalItems: allItems.length,
  weekOf: new Date().toLocaleDateString('en-GB'),
  generatedAt: new Date().toISOString(),
  bySource: {},
  allItems: allItems.sort((a, b) => {
    const dateA = new Date(a.publishedAt || 0);
    const dateB = new Date(b.publishedAt || 0);
    return dateB - dateA;
  })
};

// Add source breakdown
for (const [source, items] of Object.entries(grouped)) {
  summary.bySource[source] = {
    count: items.length,
    latest: items[0]?.title || 'N/A',
    items: items.slice(0, 10)
  };
}

// Create text summary
let textSummary = `n8n Weekly Updates - ${summary.weekOf}\n`;
textSummary += `Total: ${summary.totalItems} new items\n\n`;

const sourceEmojis = {
  release: 'üéâ Releases',
  blog: 'üì∞ Blog Posts', 
  docs: 'üìö Documentation',
  forum: 'üì¢ Announcements'
};

for (const [source, data] of Object.entries(summary.bySource)) {
  if (data.count > 0) {
    textSummary += `${sourceEmojis[source] || source}: ${data.count} items\n`;
    for (const item of data.items.slice(0, 5)) {
      const date = item.publishedAt ? new Date(item.publishedAt).toLocaleDateString('en-GB') : 'No date';
      const version = item.version ? ` (v${item.version})` : '';
      textSummary += `  ‚Ä¢ ${item.title}${version} - ${date}\n`;
      if (item.url) textSummary += `    ${item.url}\n`;
    }
    textSummary += '\n';
  }
}

return [{ json: { summary, textSummary } }];

// Simple deduplication within current execution - NO external dependencies
// This will dedupe items within this single run (not across workflow executions)

const seenIds = new Set();
const newItems = [];
const duplicateItems = [];

for (const item of $input.all()) {
  const id = item.json.id;
  if (!seenIds.has(id)) {
    newItems.push(item);
    seenIds.add(id);
  } else {
    duplicateItems.push(item);
  }
}

console.log(`Total items: ${$input.all().length}`);
console.log(`Unique items: ${newItems.length}`);
console.log(`Duplicates removed: ${duplicateItems.length}`);

if (newItems.length === 0) {
  return [{ json: { message: 'No items found', totalChecked: $input.all().length } }];
}

// Sort by date (newest first)
newItems.sort((a, b) => {
  const dateA = new Date(a.json.publishedAt || 0);
  const dateB = new Date(b.json.publishedAt || 0);
  return dateB - dateA;
});

return newItems;

// Extract versions and add tags - NO external dependencies
const versionRegex = /(?:n8n@|version |v)\s*([0-9]+\.[0-9]+\.[0-9]+)/i;

for (const item of $input.all()) {
  const data = item.json;

  // Extract version from title
  const versionMatch = data.title.match(versionRegex);
  if (versionMatch) {
    data.version = versionMatch[1];
    if (!data.tags.includes('release')) {
      data.tags.push('versioned');
    }
  }

  // Add tags based on content
  const titleLower = data.title.toLowerCase();
  if (titleLower.includes('security')) data.tags.push('security');
  if (titleLower.includes('fix')) data.tags.push('bugfix');
  if (titleLower.includes('feat') || titleLower.includes('feature')) data.tags.push('feature');
  if (titleLower.includes('breaking')) data.tags.push('breaking-change');
}

return $input.all();

// Process Atom feeds - Pure JavaScript XML parsing

// Simple hash using built-in JS only
function makeId(input) {
  let result = 0;
  for (let i = 0; i < input.length; i++) {
    result = ((result << 5) - result) + input.charCodeAt(i);
