// Simple XML text extraction - no external dependencies
function extractXMLValue(xml, tagName) {
  const regex = new RegExp(`<${tagName}[^>]*>(.*?)<\/${tagName}>`, 'gi');
  const matches = [];
  let match;
  while ((match = regex.exec(xml)) !== null) {
    matches.push(match[1].trim());
  }
  return matches;
}

function extractXMLAttribute(xml, tagName, attribute) {
  const regex = new RegExp(`<${tagName}[^>]*${attribute}=["']([^"']*)["'][^>]*>`, 'gi');
  const matches = [];
  let match;
  while ((match = regex.exec(xml)) !== null) {
    matches.push(match[1]);
  }
  return matches;
}

function parseSimpleAtom(xmlContent, sourceType) {
  const entries = [];

  // Split by entry tags
  const entryMatches = xmlContent.match(/<entry[^>]*>.*?<\/entry>/gis);
  if (!entryMatches) return entries;

  for (const entryXML of entryMatches) {
    const titles = extractXMLValue(entryXML, 'title');
    const updateds = extractXMLValue(entryXML, 'updated');
    const publisheds = extractXMLValue(entryXML, 'published');
    const summaries = extractXMLValue(entryXML, 'summary');
    const contents = extractXMLValue(entryXML, 'content');
    const hrefs = extractXMLAttribute(entryXML, 'link', 'href');

    const title = (titles[0] || '').trim();
    const url = (hrefs.find(h => !h.includes('comments')) || hrefs[0] || '').trim();
    const publishedAt = cleanDate(updateds[0] || publisheds[0]);
    const subtitle = (summaries[0] || contents[0] || '').trim().substring(0, 200);

    if (title) {
      entries.push({
        source: sourceType,
        title,
        url,
        publishedAt,
        subtitle: subtitle || null,
        version: null,
        tags: [sourceType === 'release' ? 'release' : sourceType === 'docs' ? 'docs-update' : 'dev-activity'],
        id: makeId(sourceType + title + url),
        raw: { title, url, publishedAt, subtitle }
      });
    }
  }

  return entries;
}

