parameters.jsCode:
// Get the incoming data - already schedule_entries array
const scheduleEntries = $('Proper Extract Data').first().json.schedule_entries;
const flattened = [];

// Loop over each ring object
for (const ringData of scheduleEntries) {
  const ringName = ringData.ring;

  // Inner loop: iterate over entries within that ring
  for (const entry of ringData.entries) {
    flattened.push({
      json: {
        ring: ringName,
        ...entry
      }
    });
  }
}

return flattened;

const cheerio = require('cheerio');

const html = $input.first().json.email_body;
const $ = cheerio.load(html);

const bodyText = $('body').text();

// Extract Show Date
const showDateMatch = bodyText.match(/schedule for[^\d]*(\d{2}\/\d{2}\/\d{4})/i);
const showDate = showDateMatch ? showDateMatch[1] : '';

// Convert to YYYY-MM-DD format for Supabase
const [month, day, year] = showDate.split('/');
const formattedDate = `${year}-${month}-${day}`;

return [{
  json: {
    show_date: formattedDate
  }
}];

